mapper.select:
SELECT DATE_FORMAT(out_date,'%y-%M')yearMonth ,SUM(o.out_amt*p.PRICE) totalPrice ,(SUM(o.out_amt*p.PRICE))*0.4 totalIncome FROM outbound o JOIN product p on o.product_id = p.PRODUCT_ID WHERE o.status = 'Y'
AND p.PRODUCT_NAME LIKE CONCAT ('%', #{ productName }, '%')
GROUP BY yearMonth;
